import React from 'react'
import { useEffect,useState } from 'react';
import { GoogleLogin } from 'react-google-login';
import { gapi } from 'gapi-script';

export const Login =()=>{
    
    const clientId = "871906509606-fpervph8512sk23ork06ds3hfkjb3csu.apps.googleusercontent.com";
    useEffect(() => {
        const initClient = () => {
              gapi.client.init({
              clientId: clientId,
              scope: ''
            });
         };
         gapi.load('client:auth2', initClient);
     });
      
      
    const onSuccess = (res) => {
        console.log('success:', res);
    };
    const onFailure = (err) => {
        console.log('failed:', err);
    };
  return (
    <div>
        {/* <GoogleLogin
          clientId={clientId}
          buttonText="Sign in with Google"
          onSuccess={onSuccess}
          onFailure={onFailure}
          cookiePolicy={'single_host_origin'}
          isSignedIn={true}
      /> */}

    </div>
  )
}
